import base64
import os
import sys



image_base64 = {
        'add': '',
        'delete': '',
        'search' : '',
        'update' : ''

    }

def image():
    current_path = os.path.dirname(sys.argv[0])
    new_directory_name = "img"
    new_directory_path = os.path.join(current_path, new_directory_name)
    if not os.path.exists(new_directory_path):
        os.makedirs(new_directory_path)

    for image_name, image_string in image_base64.items():
        image_data = base64.b64decode(image_string)
        with open(f"./img/{image_name}.png", 'wb') as f:
            f.write(image_data)